/*
 * Fetches a filtered (by name) list  of characters from the Rick & Morty API
 */
async function getCharacter(name) {
	console.clear();
	const data = await (await fetch(`https://rickandmortyapi.com/api/character/?name=${name}`)).json();
	data.error ? console.log(`Error: ${data.error}`) : console.log(data.results.map((x) => x.name));
}




