/**
 * Given this code...
 * How would you refactor the async operation to be more performant
 * Hint: you could trigger all at once ðŸ˜‰
 */

const endpoints = {
	character: "https://rickandmortyapi.com/api/character",
	location: "https://rickandmortyapi.com/api/location",
	episode: "https://rickandmortyapi.com/api/episode",
};

async function getDataFrom(endpoint) {
	return fetch(endpoint)
		.then((response) => response.json())
		.then((json) => json)
		.catch((error) => console.error(error));
}

(async () => {
	const characters = await getDataFrom(endpoints.character);
	const location = await getDataFrom(endpoints.location);
	const episode = await getDataFrom(endpoints.episode);

	console.log(characters, location, episode);
})();

