somePromise
	.then((value) => {
	console.log(value);
	return Promise.resolve(value * 2);
})
	.then((value) => {
	console.log(value);
	return Promise.reject(value * 2);
})
	.catch((value) => {
	console.log(value);
	return Promise.resolve(value * 2);
