/* 
	How would you fix this in order so the
	addToCount function can increase the Counter's count?
*/

const Counter = {
	count: 0,
	add: function () {
		this.count++;
	},
};

Counter.add();
console.log(Counter.count);

const addToCount = Counter.add;
addToCount();

console.log(Counter.count);

