// Ford
const ford = {
	brand: "Ford",
	speed: 0,

	accelerate: function (amount) {
		this.speed += amount;
	},

	brake: function (amount) {
		this.speed -= amount;
	},

	status: function () {
		return `${this.brand} running at ${this.speed} km/h`;
	},
};

ford.accelerate(50);
console.log(ford.status());
ford.accelerate(100);
console.log(ford.status());
ford.brake(25);
console.log(ford.status());

