const somePromise = new Promise((resolve, _) => resolve(2));

somePromise
	.then((value) => {
	console.log(value);
	return Promise.resolve(value * 2);
})
	.then((value) => {
	console.log(value);
	return Promise.reject(value * 2);
})
	.catch((value) => {
	console.log(value);
	return Promise.resolve(value * 2);
})
	.then((value) => {
	console.log(value);
	return Promise.resolve(value * 2);
});
